import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API_URL_ARTICLES = "http://localhost:5000/api/articles";

function ArticleDetailsPage() {
  const { id } = useParams(); // Get article ID from URL
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  // Fetch the article by its ID
  useEffect(() => {
    fetch(`${API_URL_ARTICLES}/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setArticle(data.article);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching article");
        setLoading(false);
        console.error("Error fetching article:", error);
      });
  }, [id]);

  // Handle deleting the article
  const handleDelete = () => {
    fetch(`${API_URL_ARTICLES}/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        navigate("/articles"); // Redirect to the articles list page after deleting
      })
      .catch((error) => {
        setError("Error deleting article");
        console.error("Error deleting article:", error);
      });
  };

  // Handle editing the article
  const handleEdit = () => {
    navigate(`/articles/edit/${id}`); // Redirect to edit page
  };

  return (
    <div>
      <h1>Article Details</h1>
      {loading ? (
        <p>Loading article...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div>
          <h2>{article.title}</h2>
          <p>{article.content}</p>
          <p><strong>Author:</strong> {article.author.name}</p>
          <p><strong>Category:</strong> {article.category.name}</p>
          <p><strong>Tags:</strong> {article.tags.join(", ")}</p>
          <p><strong>Views:</strong> {article.views}</p>
          <button onClick={handleEdit}>Edit Article</button>
          <button onClick={handleDelete}>Delete Article</button>
        </div>
      )}
    </div>
  );
}

export default ArticleDetailsPage;
