import React, { useState, useEffect } from "react";
import UserForm from "./components/UserForm";
import UserList from "./components/UserList";
import Navbar from "./components/Navbar";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import ArticleList from "./components/ArticleList";
import ArticleDetail from "./components/ArticleDetail";
import ArticleForm from "./components/ArticleForm";

const API_URL = "http://localhost:5000/api/users";

function App() {
  const [users, setUsers] = useState([]);

  // Fetch users from backend
  useEffect(() => {
    fetch(API_URL)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => setUsers(data.users))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  // Add a new user
  const addUser = (newUser) => {
    fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newUser),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => setUsers([...users, data.user]))
      .catch((error) => console.error("Error adding user:", error));
  };

  // Update an existing user
  const updateUser = (id, updatedUser) => {
    fetch(`${API_URL}/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedUser),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setUsers(users.map((user) => (user._id === id ? data.user : user)));
      })
      .catch((error) => console.error("Error updating user:", error));
  };

  // Delete a user
  const deleteUser = (id) => {
    fetch(`${API_URL}/${id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        setUsers(users.filter((user) => user._id !== id));
      })
      .catch((error) => console.error("Error deleting user:", error));
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>User Management</h1>
        <UserForm addUser={addUser} />
        <UserList users={users} updateUser={updateUser} deleteUser={deleteUser} />
        <Router>
      <Routes>
        <Route path="/" element={<ArticleList />} />
        <Route path="/articles/new" element={<ArticleForm onSubmit={() => window.location.href = "/"} />} />
        <Route path="/articles/:id" element={<ArticleDetail />} />
        <Route path="/articles/:id/edit" element={<ArticleForm onSubmit={() => window.location.href = "/"} />} />
      </Routes>
    </Router>
      </div>
    </div>
  );
}

export default App;
