import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";

const API_URL_ARTICLES = "http://localhost:5000/api/articles";

function ArticleForm() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [author, setAuthor] = useState("");
  const [category, setCategory] = useState("");
  const [tags, setTags] = useState("");
  const history = useHistory();
  const { id } = useParams(); // To check if it's an edit or new article

  // If editing an article, fetch its data
  useEffect(() => {
    if (id) {
      fetch(`${API_URL_ARTICLES}/${id}`)
        .then((response) => response.json())
        .then((data) => {
          const article = data.article;
          setTitle(article.title);
          setContent(article.content);
          setAuthor(article.author._id);
          setCategory(article.category._id);
          setTags(article.tags.join(", "));
        })
        .catch((error) => console.error("Error fetching article:", error));
    }
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newArticle = {
      title,
      content,
      author,
      category,
      tags: tags.split(","),
    };

    if (id) {
      // Update article using fetch
      fetch(`${API_URL_ARTICLES}/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newArticle),
      })
        .then((response) => response.json())
        .then(() => {
          history.push("/articles"); // Redirect to articles page
        })
        .catch((error) => console.error("Error updating article:", error));
    } else {
      // Create new article using fetch
      fetch(API_URL_ARTICLES, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newArticle),
      })
        .then((response) => response.json())
        .then(() => {
          history.push("/articles"); // Redirect to articles page
        })
        .catch((error) => console.error("Error creating article:", error));
    }
  };

  return (
    <div>
      <h1>{id ? "Edit Article" : "Create Article"}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          required
        />
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Content"
          required
        />
        <input
          type="text"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          placeholder="Author"
          required
        />
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          placeholder="Category"
          required
        />
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          placeholder="Tags (comma-separated)"
        />
        <button type="submit">{id ? "Update" : "Create"} Article</button>
      </form>
    </div>
  );
}

export default ArticleForm;
