import React, { useState, useEffect } from "react";
import UserForm from "./components/UserForm";
import UserList from "./components/UserList";
import Navbar from "./components/Navbar";

import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import ArticleMangement from "./components/ArticleMangement";
import ArticlesPage from "./components/ArticlesPage";
import ArticleDetailsPage from "./components/ArticleDetailsPage";
import ArticleForm from "./components/ArticleForm";

const API_URL = "http://localhost:5000/api/users";
const API_URL_ARTICLES = "http://localhost:5000/api/articles";

function App() {
  const [users, setUsers] = useState([]);
  const [articles, setArticles] = useState([]);
  const navigate = useNavigate(); // Using useNavigate to handle navigation

  // Fetch users from backend
  useEffect(() => {
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => setUsers(data.users))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  // Fetch articles from backend
  useEffect(() => {
    fetch(API_URL_ARTICLES)
      .then((response) => response.json())
      .then((data) => setArticles(data.articles))
      .catch((error) => console.error("Error fetching articles:", error));
  }, []);

  // Add a new user
  const addUser = (newUser) => {
    fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newUser),
    })
      .then((response) => response.json())
      .then((data) => setUsers([...users, data.user]))
      .catch((error) => console.error("Error adding user:", error));
  };

  // Add a new article
  const addArticle = (newArticle) => {
    fetch(API_URL_ARTICLES, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newArticle),
    })
      .then((response) => response.json())
      .then((data) => {
        setArticles([...articles, data.article]);
        navigate("/articles"); // Redirect to articles page after adding a new article
      })
      .catch((error) => console.error("Error adding article:", error));
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>User Management</h1>
        <UserForm addUser={addUser} />
        <UserList users={users} />
      </div>

      <div className="container">
        <h1>Article Management</h1>
        <ArticleMangement addArticle={addArticle} />
        <ArticlesPage articles={articles} />
      </div>

      <Routes>
        <Route path="/articles" element={<ArticlesPage articles={articles} />} />
        <Route path="/articles/create" element={<ArticleForm addArticle={addArticle} />} />
        <Route path="/" element={<h1>Welcome to Blog System</h1>} />
      </Routes>
    </div>
  );
}

export default App;
