import React, { useState } from "react";
import { createArticle, updateArticle } from "../services/api";

const ArticleForm = ({ article, onSubmit }) => {
  const [formData, setFormData] = useState({
    title: article ? article.title : "",
    content: article ? article.content : "",
    author: article ? article.author : "",
    category: article ? article.category : "",
    tags: article ? article.tags.join(", ") : "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const articleData = {
      ...formData,
      tags: formData.tags.split(",").map((tag) => tag.trim()),
    };
    if (article) {
      await updateArticle(article._id, articleData);
    } else {
      await createArticle(articleData);
    }
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="title"
        value={formData.title}
        onChange={handleChange}
        placeholder="Title"
        required
      />
      <textarea
        name="content"
        value={formData.content}
        onChange={handleChange}
        placeholder="Content"
        required
      />
      <input
        name="author"
        value={formData.author}
        onChange={handleChange}
        placeholder="Author ID"
        required
      />
      <input
        name="category"
        value={formData.category}
        onChange={handleChange}
        placeholder="Category ID"
        required
      />
      <input
        name="tags"
        value={formData.tags}
        onChange={handleChange}
        placeholder="Tags (comma separated)"
      />
      <button type="submit">{article ? "Update" : "Create"}</button>
    </form>
  );
};

export default ArticleForm;
