import React, { useState, useEffect } from "react";
import ArticleForm from "./ArticleForm";
import ArticleList from "./ArticleList";

const API_URL = "http://localhost:5000/api/articles"

function ArticleMangement(){

    const [articles,setArticles] = useState(null);

    // Fetch articles from our DB
    useEffect(()=>{
        fetch(API_URL).then((response)=>{
            if(!response.ok){
                throw new Error("NetWork Issue is over there");
            }
            return response.json();
        })
        .then((data)=>{
            setArticles(data.articles);
        }).catch((error)=>{console.log("Error when fetch is :", error)});
    },[]);



    const addArticle = (newArticle) =>{
        fetch(API_URL,{
            method:"POST",
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify(newArticle),
        }).then((response)=>{
            if(!response.of){
                throw new Error("Network response was not ok");
            }

            return response.json();
        }).then((data)=>setArticles([...articles,data.articles]))
        .catch((error)=>{ console.log("here is the error" + error)});
    };


    return(
        <div className="container">
            <h1>ArticleMangement</h1>
            
        </div>
    );
};

export default ArticleMangement;
