import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const API_URL_ARTICLES = "http://localhost:5000/api/articles";

function ArticlesPage() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch articles from backend using fetch
  useEffect(() => {
    fetch(API_URL_ARTICLES)
      .then((response) => response.json())
      .then((data) => {
        setArticles(data.articles);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching articles");
        setLoading(false);
        console.error("Error fetching articles:", error);
      });
  }, []);

  return (
    <div>
      <h1>Articles</h1>
      {loading ? (
        <p>Loading articles...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {articles.map((article) => (
            <li key={article._id}>
              <h2>{article.title}</h2>
              <p>{article.content.substring(0, 150)}...</p> {/* Display a preview */}
              <p><strong>Author:</strong> {article.author.name}</p>
              <p><strong>Category:</strong> {article.category.name}</p>
              <p><strong>Tags:</strong> {article.tags.join(", ")}</p>
              <p><strong>Views:</strong> {article.views}</p>
              {/* Link to the article details/edit page */}
              <Link to={`/articles/${article._id}`}>View/Edit Article</Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ArticlesPage;
